
public class Ders4_Ornek7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int sayi=250;
		int count=++sayi;
		System.out.println("sayi="+sayi+" count="+count);
		count=--sayi;
		System.out.println("sayi="+sayi+" count="+count);
		count/=5;
		System.out.println("sayi="+sayi+" count="+count);
		sayi*=2;
		System.out.println("sayi="+sayi+" count="+count);
		
	}

}
