
public class Person extends Hastane {

	
	private String ad;
	private int yas;
	public Person() {
		// TODO Auto-generated constructor stub
	}
	public Person(String ad, int yas) {
		super();
		this.ad = ad;
		this.yas = yas;
	}
	public String getAd() {
		return ad;
	}
	public void setAd(String ad) {
		this.ad = ad;
	}
	public int getYas() {
		return yas;
	}
	public void setYas(int yas) {
		this.yas = yas;
	}
	
	
	
}
