package ders14;

public class Ornek_11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		Carpim tablosu yapiniz
//		1x1=1
//		1x2=2
		for (int i = 1; i <= 10; i++) {
			for (int j = 1; j <= 10; j++) {
				System.out.println(i+ " X "+j+" = "+(i*j));
			}
			System.out.println("*****************************");
		}
		
	}

}
